member.example:

 [C#] 
                
try
{
   // Create an HDF5 file.
   H5FileId fileId = H5F.create("myCSharp.h5", 
                                 H5F.CreateMode.ACC_TRUNC);
   H5F.close(openId);
}
catch (HDFException e)
{
   Console.WriteLine(e.Message);
}


 [Visual Basic .NET] 
Imports HDF5DotNet

Module Module1
 Sub Main()
     Dim FileId As H5FileId
        Try
            FileId = H5F.create("HDF5_fromVB", _
                                 H5F.CreateMode.ACC_TRUNC)
            H5F.close(FileId)
        Catch ex As HDFException
            Console.WriteLine(ex.Message())
        End Try
        Console.WriteLine("Processing complete!")
        Console.ReadLine()
    End Sub
End Module

 [C++/Cli] 
try
{
  // Create an HDF5 file.
  H5FileId^ fileId = H5F::create("myCpp.h5", 
	                         H5F::CreateMode::ACC_TRUNC);
  // Close the file
  H5F::close(fileId);
}
catch (HDFException^ e)
{
   Console::WriteLine(e->Message);
}


 [C#] 
                
try
{
   // Create an HDF5 file.
   H5FileId fileId = H5F.create("myCSharp.h5", 
                                 H5F.CreateMode.ACC_TRUNC);
   // Create a HDF5 group.  
   H5GroupId groupId = H5G.create(fileId, "/cSharpGroup", 0);
   H5G.close(groupId);
   H5F.close(openId);
}
catch (HDFException e)
{
   Console.WriteLine(e.Message);
}


 [Visual Basic .NET] 
Imports HDF5DotNet

Module Module1
 Sub Main()
     Dim FileId As H5FileId
     Dim GroupId As H5GroupId

        Try
            FileId = H5F.create("HDF5_fromVB", H5F.CreateMode.ACC_TRUNC)
            GroupId = H5G.create(FileId, "/vbGroup", _
                                H5F.CreateMode.ACC_TRUNC)
            
            H5G.close(GroupId)
            H5F.close(FileId)
        Catch ex As HDFException
            Console.WriteLine(ex.Message())

        End Try
        Console.WriteLine("Processing complete!")
        Console.ReadLine()
    End Sub
End Module

 [C++/Cli] 
try
{
  // Create an HDF5 file.
  H5FileId^ fileId = H5F::create("myCpp.h5", 
	                         H5F::CreateMode::ACC_TRUNC);
  // Create a HDF5 group.  
  H5GroupId^ groupId = H5G::create(fileId, "/cppGroup", 0);

  // Close the group.
  H5G::close(groupId);

  // Close the file
  H5F::close(fileId);
}
catch (HDFException^ e)
{
   Console::WriteLine(e->Message);
}

member.remarks:
H5Screate creates a new dataspace of a particular type. The
types currently supported are H5SClass.SCALAR and H5SClass.SIMPLE;
others are planned to be added later.
